import './App.css';
import React, { Component } from "react";
import { useState } from "react";
import { useForm } from "react-hook-form";




// get the SHA256 module and a salt
var salt = '2irjv34vawirj2jwjvwjreka';
var SHA256 = require("crypto-js/sha256");
var typedname = ""; 
var typedpassword = ""; 


//get input from the login form


function Login() {
  const {register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = (data) => console.log(data);

  return (
    <>
      <div className="loginstuff">
          <div className="loginform">


      <form className="Login" onSubmit={handleSubmit(onSubmit)}>
      <div className="blacktext1">Username</div>
      <div className="form1">
        <input type="text" {...register("name")} />
        </div>
        <br></br>
        <div className="blacktext2">Password </div>
        <div className="form2">
        <input type="USERNAME" {...register("email", { required: true })} />
        {errors.email && <span style={{ color: "red" }}>
        *Email* is mandatory </span>}
        </div>
        <div className="send">


        <input type={"submit"}  value="Submit" />
        </div>
      </form>
      </div>
      </div>
    </>
  );
}



export default Login;
