import React, {useState, useEffect } from "react";
import axios from './axios';
import './App.css';

function Comments() {
  const [data, getData] = useState([])
  const URL = 'http://localhost:3000/comments';


  useEffect(() => {
      fetchData()
  }, [])


  const fetchData = () => {
      fetch(URL)
          .then((res) =>
              res.json())

          .then((response) => {
              console.log(response);
              getData(response);
          })

  }

  return (
<>



<div className='commentthingy'>
          <tbody>
              <tr>
                  <th>Comment ID</th>
                  <th>Name</th>
                  <th>Comment</th>

              </tr>

              {data.map((item, i) => (
                  <tr key={i}>
                      <td>{item._id}</td>
                      <td>{item.name}</td>
                      <td>{item.commentmsg}</td>



                  </tr>
              ))}
          </tbody>
          </div>






</>
  );
}

export default Comments;
