import React, {useRef,useState, useEffect } from "react";
import axios from './axios';
import './App.css';
import useRefreshToken from "./hooks/useRefreshToken";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
function Comments() {

  const [success, setSuccess] = useState(false);
  const userRef = useRef();
  const errRef = useRef();
  const [errMsg, setErrMsg] = useState("");
  const [data, getData] = useState([]);
  const [name, setName] = useState("");
  const [commentmsg, setCommentmsg] = useState("");
  const URL = 'http://localhost:3000/comments';
  const refresh = useRefreshToken();
  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrMsg("");
  }, [name, commentmsg]);


  const handleSubmit = async (e) => {
    e.preventDefault();


    try {
      const response = await axios.post(
        URL,
        JSON.stringify({ name, commentmsg}),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true
        }
      );

      setName("");
      setCommentmsg("");

      setSuccess(true);

    } catch (err) {
      if (!err?.response) {
        setErrMsg("No Server Response");
      }
      errRef.current.focus();
    }
  };

  useEffect(() => {
      fetchData()
  }, [])


  const fetchData = () => {
      fetch(URL)
          .then((res) =>
              res.json())

          .then((response) => {
              console.log(response);
              getData(response);
          })

  }

  return (
<>
{success ? (
        <section>
          <br></br>
          <h1>Comment Created!</h1>
        </section>
      ) : (
        <section>
 <div className="commentform">
            <p
              ref={errRef}
              className={errMsg ? "errmsg" : "offscreen"}
              aria-live="assertive"
            >
              {errMsg}
            </p>
            <div className="createacommentbutton">
              <h1>Add a comment!</h1>
            </div>



            <form onSubmit={handleSubmit}>
              <div className="blacktext1">
                <label htmlFor="name">Name:</label>
                <input
                  type="text"
                  id="name"
                  ref={userRef}
                  autoComplete="off"
                  onChange={(e) => setName(e.target.value)}
                  value={name}
                  required
                  aria-describedby="uidnote"
                />
              </div>
              <div className="blacktext2">
                <label htmlFor="commentMsg">Comment:</label>
                <input
                  type="text"
                  id="commentmsg"
                  onChange={(e) => setCommentmsg(e.target.value)}
                  value={commentmsg}
                  required
                />
              </div>


              <div className="adddog">
                <button onClick={() => refresh()}>Create comment!</button>
              </div>
            </form>
            <p>
              <span className="line"></span>
            </p>
          </div>



<div className="commentthingy">
<tbody>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Comment</th>

        </tr>
        {data.map((item, i) => (
          <tr key={i}>
            <td>{item._id}</td>
            <td>{item.name}</td>
            <td>{item.commentmsg}</td>
            <td>{item.location}</td>

          </tr>
        ))}
      </tbody>

      </div>

        </section>

      )}
    </>
  );
}

export default Comments;
