import React, {useState, useEffect } from "react";
import axios from './axios';
import './App.css';

function Homepage() {
  const [data, getData] = useState([])
  const URL = 'http://localhost:3000/comments';


  useEffect(() => {
      fetchData()
  }, [])


  const fetchData = () => {
      fetch(URL)
          .then((res) =>
              res.json())

          .then((response) => {
              console.log(response);
              getData(response);
          })

  }

  return (
<>

<div className='loginstuffv2'>
          <tbody>
              <tr>
                  <th>Dog ID</th>
                  <th>Name</th>
                  <th>Breed</th>

              </tr>

              {data.map((item, i) => (
                  <tr key={i}>
                      <td>{item._id}</td>
                      <td>{item.name}</td>
                      <td>{item.comment}</td>
                      <td>{item.location}</td>
                      <div className='imagesinsidetable'>
                      <td><img class="img3" src={`http://localhost:3000/img/${item.img}.jpg`}></img></td>
                      </div>

                  </tr>
              ))}
          </tbody>
          </div>

<div className="homepagestuff">
<div>
<div className="floattext1">
WELCOME!
</div>

<div className="floattext2">
The Canine Shelter of Hong Kong was formed in 1999, and our mission is formed on the basis that animals should receive the best welfare from our society, that's why we formed THE CANINE SHELTER. Here, you can browse our aavilable dogs and learn the information of each animal, and in which they are up to adoption! 
</div>

<img src="indexdog.jpg"> 
</img>

</div>
</div>


</>
  );
}

export default Homepage;
