import './App.css';
import './dogspage.css';
import React, {useState, useEffect } from "react";
import axios from './axios';
const DOG_URL = "/dogs";


function Browsedogs() {

  const userRef = useRef();
  const errRef = useRef();
  const [errMsg, setErrMsg] = useState("");
  const [data, getData] = useState([]);
  const URL = "http://localhost:3000/dogs";
  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [breed, setBreed] = useState("");
  const [location, setLocation] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
      fetchData()
  }, [])


  const fetchData = () => {
      fetch(URL)
          .then((res) =>
              res.json())

          .then((response) => {
              console.log(response);
              getData(response);
          })

  }

  const refresh = useRefreshToken();
  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrMsg("");
  }, [id, name, breed, location]);


  const handleSubmit = async (e) => {
    e.preventDefault();


    try {
      const response = await axios.put(
        DOG_URL,
        JSON.stringify({ id, name, breed, location }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true
        }
      );

      setId("");
      setName("");
      setBreed("");
      setLocation("");

      setSuccess(true);

    } catch (err) {
      if (!err?.response) {
        setErrMsg("No Server Response");
      }
      errRef.current.focus();
    }
  };

  return (
      <>
       <div className='loginstuffv2'>
          <tbody>
              <tr>
                  <th>Dog ID</th>
                  <th>Name</th>
                  <th>Breed</th>
                  <th>Location</th>
                  <th>img</th>
              </tr>

              {data.map((item, i) => (
                  <tr key={i}>
                      <td>{item._id}</td>
                      <td>{item.name}</td>
                      <td>{item.breed}</td>
                      <td>{item.location}</td>
                      <div className='imagesinsidetable'>
                      <td><img class="img3" src={`http://localhost:3000/img/${item.img}.jpg`}></img></td>
                      </div>

                  </tr>
              ))}
          </tbody>
          </div>

      </>
  );
}




export default Browsedogs;
