import './App.css';
import React, {useRef, useState, useEffect } from "react";
import axios from './axios';



function Adminpanel() {
    const userRef = useRef();
    const errRef = useRef();
    const [name, setname] = useState('');
    const [breed, setbreed] = useState('');
    const [location, setlocation] = useState('');
    const [img, setimg] = useState('');

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd, staffcode])

    const handleSubmit = async (e) => {
        e.preventDefault();
        alert('You have input in '+[user]+' as username');
        alert('You have input in '+[pwd]+' as password');
        alert('You have input in '+[staffcode]+' as staffcode');
        alert('Your registration request is now sent to server for processing');

        try {
            const response = await axios.post(REGISTER_URL,
                JSON.stringify({ user, pwd, staffcode}),
                {
                    headers: { 'Content-Type': 'application/json' },
                    withCredentials: true
                }
            );
            setSuccess(true);

            setUser('');
            setPwd('');
            setStaff('');

        } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 409) {
                setErrMsg('Username Taken');
            } else {
                setErrMsg('Registration Failed')
            }
            errRef.current.focus();
        }
    }

  const [data, getData] = useState([])
  const URL = 'http://localhost:3000/dogs';

  useEffect(() => {
      fetchData()
  }, [])


  const fetchData = () => {
      fetch(URL)
          .then((res) =>
              res.json())

          .then((response) => {
              console.log(response);
              getData(response);
          })

  }

  return (
      <>

          <tbody>
              <tr>
                  <th>Dog ID</th>
                  <th>Name</th>
                  <th>Breed</th>
                  <th>Location</th>
                  <th>img</th>
              </tr>
              {data.map((item, i) => (
                  <tr key={i}>
                      <td>{item._id}</td>
                      <td>{item.name}</td>
                      <td>{item.breed}</td>
                      <td>{item.location}</td>
                      <td>{item.img}</td>

                  </tr>
              ))}
          </tbody>

          <div className='POSTadog'>


            POST a dog


          </div>

      </>
  );
}




export default Adminpanel;
