    setErrMsg("");
  }, [name, commentmsg]);


  const handleSubmit = async (e) => {
    e.preventDefault();


    try {
      const response = await axios.post(
        URL,
        JSON.stringify({ name, commentmsg}),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true
        }
      );

